INSERT INTO `content_terms_of_use_entries` (`id`, `name`, `position`, `description`, `student_description`, `download_condition`, `icon`, `is_default`, `mkdate`, `chdate`) VALUES('FREE_LICENSE', 'Werk mit freier Lizenz', 3, 'Werke, die unter einer freien Lizenz ver√∂ffentlich wurden, d.h. deren Weitergabe und zumeist auch Ver√§nderung ohne Lizenzkosten gestattet ist, d√ºrfen Sie ohne Einschr√§nkungen f√ºr den Unterricht zug√§nglich machen. \n\nTypische Beispiele sind:\n- Open-Access-Publikationen \n- Open Educational Ressources (OER) \n- Werke unter Creative-Commons-Lizenzen (z.B. Wikipedia-Inhalte) \n\nAchtung: Vergewissern Sie sich im Einzelfall, welche Einschr√§nkungen f√ºr die Verbreitung und Ver√§nderung die jeweilige Lizenz ggf. enth√§lt.', 'Das Dokument unterliegt einer freien Lizenz. Sie d√ºrfen es weitergeben und unter Beachtung der Details der Lizenz (s. Angaben im Dokument) ver√§ndern und in eigene Werke √ºbernehmen.', 0, 'cc', 0, 1499435049, 1499435049);
